import{_ as a,c as t,o as n,j as e,a as o}from"./chunks/framework.Cv3JQ2Vj.js";const u=JSON.parse('{"title":"更换成了自己开发的博客","description":"","frontmatter":{"title":"更换成了自己开发的博客","date":"2018-08-06T00:00:00.000Z"},"headers":[],"relativePath":"posts/geng-huan-cheng-le-zi-ji-kai-fa-de-bo-ke.md","filePath":"posts/geng-huan-cheng-le-zi-ji-kai-fa-de-bo-ke.md"}'),s={name:"posts/geng-huan-cheng-le-zi-ji-kai-fa-de-bo-ke.md"},i=e("h1",{id:"更换成了自己开发的博客",tabindex:"-1"},[o("更换成了自己开发的博客 "),e("a",{class:"header-anchor",href:"#更换成了自己开发的博客","aria-label":'Permalink to "更换成了自己开发的博客"'},"​")],-1),r=e("p",null,"很久以前就再想，用上自己写的博客，这次也算是实现了吧，总体感觉呢，其实就是仿微信，用的也是官方微信开源的库罢了。前几天偶然看到了微信开源的库，除此之外还有QQ、支付宝、淘宝、旺信的库。当时的一段时间正好在学laravel，一直想写个项目练练手，于是就想着用wx的库打造一个博客试一试，之所以选wx也是因为喜欢wx的那种简约感，花了两天的时间。架构也就差不多拼好了，也就是现在看到的样子。前端模板搞定了后，再就是后端的事情了，因为是第一次用laravel，踩了很多的坑太多太多，这个我准备再写一篇文章吐吐槽，不过带来的收获也非常的大，感觉知识面进步了不少，最后也同样花了2天的时间写完了后端。至此larvael的处女作也就完成了，然后我又花了一天的时间用TP同样的又写了一遍。为的也是练手，因为laravel的关系，再用tp的时候我就知道了，原来还可以自定义路由，在tp上，我也是类似laravel的感觉开发的。感觉用的非常爽。再说说其他的吧。项目完成后，本来没想实际用上的，只是练手项目学框架罢了，但后来感觉好像还不错就用上了。但用上的过程也并不轻松阿。环境的问题加上数据迁移的问题也是折腾了2天而且数据问题还没完美。感觉上线的好不容易。不过实际用上了自己写的博客的时候，强烈的满足感！再来说说布局吧，全站采用响应式布局。适配各种终端。包括后台！功能也比较简单。够自己使用。总的来说，项目虽小，但学的知识点到不少。我也只是站在巨人的肩膀上才能这么快的完成，不然要我自己完全从0开始，肯定没这么快这么的简单。",-1),l=[i,r];function c(d,h,p,_,g,f){return n(),t("div",null,l)}const k=a(s,[["render",c]]);export{u as __pageData,k as default};
