import{_ as n,c as o,o as t,j as e}from"./chunks/framework.Cv3JQ2Vj.js";const w=JSON.parse('{"title":"记重装web环境所遇到的问题","description":"","frontmatter":{"title":"记重装web环境所遇到的问题","date":"2017-02-25T00:00:00.000Z"},"headers":[],"relativePath":"posts/ji-zhong-zhuang-web-huan-jing-suo-yu-dao-de-wen-ti.md","filePath":"posts/ji-zhong-zhuang-web-huan-jing-suo-yu-dao-de-wen-ti.md"}'),a={name:"posts/ji-zhong-zhuang-web-huan-jing-suo-yu-dao-de-wen-ti.md"},i=e("p",null,"今天感觉没事做，就决定重装web环境。重装的原因是感觉自己的博客访问速度有点慢。(我自己感觉慢而已)。于是有点怀疑是不是自己搭建的环境的问题。(环境是我自己搭建的 IIS+PHP+Mysql)最终决定换个环境试试。",-1),s=e("p",null,"在环境服务市场里面找了一个PHP环境的镜像，当然咯还是熟悉的套路，win2008+IIS+PHP+Mysql不过加上了一个管理助手和phpMyAdmin，虽然想换lamp但是也只是想想，因为不会用linux。虽然有主机面板，但还是不想这么用，扯远了，回到主题，安装完了系统后。就是把先前备份的文件与SQL数据库还原了，问题也就是在这个时候出现了，还原完了文件和数据库IIS一些操作也做了，但是就是访问不了。一直显示500. 怀疑是web.config这个文件的问题，于是就删了这个文件，再次访问不出500了而是404，排查了一遍发现是默认首页没设置index.php。加上后倒是能访问了，但是由于把web.config删了所以文章无法访问。去后台伪静态那里又生成了一个web.config。但只要一有规则文件就显示500，搞了半天，度了半天。想了半天最后灵光一闪，想起好像没有安装IIS伪静态组件，去IIS里面看果然没有URL重写，又从微软官方下载IIS URL Rewrite安装上，然后重新生成了web.config访问了下，一切正常。这么简单的事情。花费了我一个小时多的时间，真是不值得。 最关键的事情是，发现换了个环境后 访问速度我还是觉得慢。没有提升，甚至有点觉得还不如以前了。",-1),u=[i,s];function c(d,p,h,r,l,g){return t(),o("div",null,u)}const f=n(a,[["render",c]]);export{w as __pageData,f as default};
