import{_ as t,c as a,o as s,j as e,a as o}from"./chunks/framework.Cv3JQ2Vj.js";const z=JSON.parse('{"title":"折腾主题","description":"","frontmatter":{"title":"折腾主题","date":"2017-12-14T00:00:00.000Z"},"headers":[],"relativePath":"posts/zhe-teng-zhu-ti.md","filePath":"posts/zhe-teng-zhu-ti.md"}'),n={name:"posts/zhe-teng-zhu-ti.md"},r=e("h1",{id:"折腾主题",tabindex:"-1"},[o("折腾主题 "),e("a",{class:"header-anchor",href:"#折腾主题","aria-label":'Permalink to "折腾主题"'},"​")],-1),c=e("p",null,"今天又花了几个小时的时间折腾了下主题，就是更换主题的背景图片，本来主题的背景图片就是随机的，但是也就固定的四张图片用的随机函数每次访问页面就随机1-4张图片做为背景图片，看久了后就觉得看腻了，于是想更换背景图片，在网上找图片又是一个折磨人的事情，于是想着有没有什么随机图片的API或者是直接用如百度，谷歌，一些大厂的图床随机找一个当成背景，百度后的确是发现了有这样随机图片的API，但多数都很慢，后来偶尔看到了获取必应每日图片的Demo 于是就将必应的每日图片当成了自己主题的背景，每天都会更换，而且必应的图片还挺好看的，现在唯一的问题就是 如果引用必应的图片 那么每次访问就会给必应发送请求，频繁后必应或许就会防盗链、封IP，，这样就没得玩了，所以我想的是 每日获取必应背景图片 然后保存到本地或者哪个云存储里面，这样再引用就不会频繁的给必应发送请求了，一天过后，再次获取新的保存 删掉前一天的。这样就能完美的解决问题了。",-1),i=[r,c];function d(h,_,l,p,m,u){return s(),a("div",null,i)}const P=t(n,[["render",d]]);export{z as __pageData,P as default};
